sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const output = api.data.sla_timer_config_api_1.output.result.config;
    const slaConfig = api.state.initialSLAConfig;
    const taskId = api.context.props.sysId;

    var slaIds = [];
    let slaConfigNew = slaConfig.map((config) => {
        const id = config.configId;
        const slaId = output[id][taskId];
        slaIds.push(slaId);
        return { ...config,
            "slaId": slaId
        };
    });
    api.setState('slaIds', slaIds);
    api.setState('slaConfig', slaConfigNew);
}
