sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    if(event.context.item.value.typeValue == 'microsoft_teams_chat'){
        const fields = {
            table: 'interaction',
            sysId: event.context.item.value.sysId
        };
        const params = {};
        params.chatRecordSysId = api.data.get_record_sysid_1.output.sysId;
        api.emit('NAV_ITEM_SELECTED', {
            route: "ms-teams-import",
            fields,
            params,
            targetRoute: 'current'
        });
    }
    else{
        helpers.navigate.to('record', {'table': 'interaction', 'sysId': event.context.item.value.sysId},{},false,false,'current');
    }

}
