sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
const roster = event.payload.roster;
if (roster.source === 'shift-details') 
    api.setState('shiftDetailsInstance', roster.shiftDetailsInstance);

else if (roster.source === 'search-section')
    api.setState('groupsDetailsInstance', roster.groupsDetailsInstance);

api.emit('MACROPONENT_VIEWPORT_LOAD_REQUESTED', {
    viewportElementId: 'experts_on_call_viewport',
    route: 'contact',
    fields: {'rosterName': roster.name, 'userName':roster.member.user.displayName},
    params: null
});
}
