sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    api.setState('hideLoader', false);
    let currentRotas = api.state.nextRotas;
    let nextRotas = api.data.escalations_get_next_rota.output.result;

    if (currentRotas.length > 0 ) {
        let mappedNextRotas = {};
        currentRotas.forEach(rota => mappedNextRotas[rota.rota_id + rota.start] = rota);
        nextRotas = nextRotas.filter(rota => !mappedNextRotas[rota.rota_id + rota.start]);
    }

    const latestNextRotas = [...currentRotas, ...nextRotas];
    api.setState('nextRotas', latestNextRotas);

    const nextRotaDetails = latestNextRotas[api.state.showingRotaIndex];
    if (nextRotaDetails) {
        api.setState('rota', nextRotaDetails.rota_id);
        api.setState('startDate', nextRotaDetails.start);
        api.setState('rosters', []);
        api.setState('managerDetails', {});
    }
}
