sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers 
*/
function handler({api, event, helpers, imports}) {
    let shiftDetailsInstance = api.state.shiftDetailsInstance;
    let groupsDetailsInstance = api.state.groupsDetailsInstance;
    if (event.payload.source === 'clean-shifts') {
        shiftDetailsInstance = {};
        api.setState('shiftDetailsInstance', shiftDetailsInstance);
    }

    if (event.payload.source === 'clean-groups') {
        groupsDetailsInstance = {};
        api.setState('groupsDetailsInstance', groupsDetailsInstance);
    }

    let compRoute = '';
    if (shiftDetailsInstance && shiftDetailsInstance.source === 'shift-details') {
        compRoute = 'shift-details';
    } else if (groupsDetailsInstance && groupsDetailsInstance.source === 'search-section') {
        compRoute = 'oncall-groups-search';
    } else compRoute = 'expertsoncallsearchandresults';

    api.emit('MACROPONENT_VIEWPORT_LOAD_REQUESTED', {
        viewportElementId: 'experts_on_call_viewport',
        route: compRoute,
        fields: null,
        params: null
    });
}
