sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
const route = 'shift-details';

if (event.payload.shift && event.payload.shift.source === 'search-section') {
    api.setState('groupsDetailsInstance', event.payload.shift.groupsDetailsInstance);
}

api.setState('group', event.payload.group.sys_id);
api.setState('shiftStartDateTime', event.payload.shiftStartDateTime);

api.emit('MACROPONENT_VIEWPORT_LOAD_REQUESTED', {
    viewportElementId: 'experts_on_call_viewport',
    route: route,
    fields: null,
    params: null
});
}
