sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    let rosters = [];
    api.setState('hideLoader', true);

    let managerDetails = {};

    if (api.data.oncall_escalations.output && api.data.oncall_escalations.output.result) {
        const _group = api.data.oncall_escalations.output.result.groupOnCallMembers[0];
        if (_group && _group.rota && _group.rota.rosters) {
            _group.rota.rosters.forEach(function(roster) {
                if (roster && roster.member && roster.member.user && roster.member.user.sys_id) {
                    let isManager = false;
                    if (_group.manager.indexOf(roster.member.user.sys_id) !== -1)
                        isManager = true;
                    roster.member.isManager = isManager;
                    roster.member.user.displayName = roster.member.user.name;
                    rosters.push(roster);
                }
            });
        }
        if (_group && _group.rota)
            api.setState('preferedFormatDate', {...api.data.oncall_escalations.output.result.curRotaDetails.formattedDateTime});

        const groupDetails = api.data.oncall_escalations.output.result.groupDetails;
        managerDetails = {
            name: "Manager",
            member: {
                user: {...groupDetails.manager.managerDetails, displayName: groupDetails.manager.managerDetails.name}
            }
        };
    }

    api.setState("managerDetails", managerDetails);
    api.setState('isCurrentTimeShift', api.data.oncall_escalations.output.result.curRotaDetails.isCurrentTimeShift);
    api.setState('rosters', rosters);
}
