sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    if (api.context.props.table == 'incident') {
        const SLAConfig = api.data.record_information.output.slaConfig;
        let configIds = SLAConfig.map((config) => {
            return config.configId;
        });

        api.setState("initialSLAConfig", SLAConfig);
        api.setState("configIds", configIds.join());

        if (configIds.length > 0) {
            // Set config first because emapty config means: First to breach
            api.setState("slaTimerCfgSysId1", configIds[0]);
            api.setState("slaTimerCfgSysId2", configIds.length > 1 ? configIds[1] : '');
            api.setState("taskSysId", api.context.props.sysId);
        }
    }
}
