sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {

    if(api.data.record_information.output && api.data.record_information.output.incidentInfo) {

        var modalOpen  = (api) => {
            api.emit('MACROPONENT_VIEWPORT_LOAD_REQUESTED', {
                viewportElementId: 'modal_viewport',
                route: 'sowformmodal',
                fields: {
                    table: api.context.props.table,
                    sysId: api.context.props.sysId,
                    title: "Assign",
                },
                params: {
                    "modalPath": "assign_modal",
                    "fields": JSON.stringify(api.context.props.fields),
                    "requiredFields": ["assignment_group", "assigned_to", "work_notes"],
                    "isGFormSave": "true",
                }
            });
        };

        if ((api.data.record_information.output.incidentInfo.assignmentGroup && api.data.record_information.output.incidentInfo.assignedTo)
            || event.elementId === 'reassign_button' || api.data.record_information.output.incidentInfo.assignedTo) {
            modalOpen(api);
        }
        else if(api.state.userhasGrp || (!api.data.record_information.output.incidentInfo.assignmentGroup && !api.data.record_information.output.incidentInfo.assignedTo)) {
            api.emit('SOW_RECORD_UI_ACTION#EXECUTE_UI_ACTION', {
                actionSysId : "8513fcd0773b301027aae297cd5a9968"
            });
        }
        else {
            modalOpen(api);
        }
    }
}
