sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
async function handler({api, event, helpers, imports}) {
    if (api.state.showAllContributors) {
        api.setState('contributorsInfo', api.data.record_information.output.contributorsInfo.slice(0, 5));
        var message = await helpers.translate('+ {0} more', (api.data.record_information.output.contributorsInfo.length-5));
        api.setState('contributorsLinkText', message);
        api.setState('showAllContributors', false);
    } else {
        api.setState('contributorsInfo', api.data.record_information.output.contributorsInfo);
        var message = await helpers.translate('show less');
        api.setState('contributorsLinkText', message);
        api.setState('showAllContributors', true);
    }
}
