sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
async function handler({api, event, helpers, imports}) {
    if (api.context.props.table == 'incident') {
        const count = api.data.record_information.output.slaInfo.count;
        const slaIds = api.state.slaIds;
        const slaIdCount = slaIds.filter(Boolean).length;

        let actualMoreSLA = count - slaIdCount;
        api.setState('actualMoreSLA', actualMoreSLA);
        const viewMoreText = await helpers.translate('View {0} more SLAs', actualMoreSLA);
        api.setState('slaViewMoreText', viewMoreText);
    }
}
