sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
var lookupResponse = api.data.lp_record.result;
if (api.state.openedForSysId !== "-1" && lookupResponse) {
    api.setState("userTimezone", lookupResponse.time_zone.displayValue);
    var callerID = api.state.openedForSysId;
    var commonPart = '^sys_created_on>javascript:gs.beginningOfLast30Days()';
    var inc = "sys_class_name=incident^ref_incident.caller_id=" + callerID + commonPart;
    var prb = "sys_class_name=problem^opened_by=" + callerID + commonPart;
    var chg = "sys_class_name=change_request^ref_change_request.requested_by=" + callerID + commonPart;
    var scr = "sys_class_name=sc_request^ref_sc_request.requested_for=" + callerID + commonPart;

    api.setState('taskQuery', inc + "^NQ" + prb + "^NQ" + chg + "^NQ" + scr + "^ORDERBYDESCsys_created_on");
}
}
