sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    if (api.data.get_recommended_groups) {
        var recommendedGroups = [];
        if (api.data.get_recommended_groups.output.result) {
            recommendedGroups = Object.keys(api.data.get_recommended_groups.output.result);
        }

        api.setState('onCallDateTime', '');
        api.setState('recommendedGroups', recommendedGroups.join(','));

        if (recommendedGroups && recommendedGroups.length > 0) {
            // Dispatch event to show the search link
            api.emit('RECOMMENDED_GROUPS_LOADED', {'hideSearchLink': true});
        } else {
            api.setState('onCallLoading', false);
            // Dispatch event to hide the search link
            api.emit('RECOMMENDED_GROUPS_LOADED', {'hideSearchLink': false});
        }
    }
}
