sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
 function evaluateProperty({api, helpers}) {
    if( api.data.record_information.output && api.data.record_information.output.incidentInfo) {
        if (api.data.record_information.output.incidentInfo.assignmentGroup && api.data.record_information.output.incidentInfo.assignedTo) return helpers.translateSync("Reassign");
        if(api.data.record_information.output.incidentInfo.assignmentGroup) {
            if(api.state.userhasGrp) return helpers.translateSync("Assign to me");
            else return helpers.translateSync("Assign");
        }
        if(api.data.record_information.output.incidentInfo.assignedTo) return helpers.translateSync("Reassign");
        return helpers.translateSync("Assign to me");
    }
}
