sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    var fieldsMap = [];
    if (event.name === 'MACROPONENT_PROPERTY_CHANGED' && event.payload.name === 'hideShiftsWithNoOnCall') {
        api.emit("RECOMMENDED_GROUPS_REFRESH");
        return;
    }

    if (api.context.props.recommendedGroupFields) {
        for (let field in api.context.props.recommendedGroupFields) {
            if (api.context.props.recommendedGroupFields.hasOwnProperty(field) && api.context.props.recommendedGroupFields[field]) {
                fieldsMap.push({
                    field: field,
                    value: api.context.props.recommendedGroupFields[field]
                });
            }
        }
    }

    api.setState("groupFieldValues", fieldsMap);
}
