sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
  const groupsDetailsInstance = api.context.props.groupsDetailsInstance;
  if (groupsDetailsInstance && Object.keys(groupsDetailsInstance).length) {
      api.setState('onCallLimit', groupsDetailsInstance.onCallLimit);
      api.setState('onCallGroup', groupsDetailsInstance.onCallGroup);
      api.setState('onCallDeepData', JSON.parse(groupsDetailsInstance.onCallDeepData));
      api.setState('onCallFlatData', JSON.parse(groupsDetailsInstance.onCallFlatData));
      api.setState('onCallOffset', groupsDetailsInstance.onCallOffset);
      api.setState('onCallSearchVal', groupsDetailsInstance.onCallSearchVal);
      api.setState('loading', false);
      api.setState('selectedIndex', groupsDetailsInstance.selectedIndex);
  }
  api.data.look_up_user_preferences.refresh();
}
