sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    api.setState('hideLoader', false);
    const showingRotaIndex = api.state.showingRotaIndex - 1;
    api.setState('showingRotaIndex', showingRotaIndex);
    const prevRotaDetails = api.state.nextRotas[showingRotaIndex];

    api.setState('rota', prevRotaDetails.rota_id);
    api.setState('isCurrentTimeShift', prevRotaDetails.isCurrentTimeShift);
    api.setState('startDate', prevRotaDetails.start);
    api.setState('rosters', []);
    api.setState('managerDetails', {});
    api.setState('preferedFormatDate', {});
}
