sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({
api,
event,
helpers,
imports
}) {
if (api.data.fetch_channel_config_data.output.isTokenAvailable) {
api.setState('loading', false); 
if (api.state.selectedRequestor === event.context.item.value) api.data.channel_redirect_utils.refresh();
else api.setState('selectedRequestor', event.context.item.value);
}
else {
api.emit('MACROPONENT_VIEWPORT_LOAD_REQUESTED', {
    viewportElementId: "modal_viewport_chat",
    route: "start-msteams-chat-modal",
    fields: null,
    params: {
        table: api.context.props.table,
        sysId: api.context.props.sysId,
        descriptionField: "short_description",
        preSelectedParticipants: event.context.item.value.id
    }
});
}
}
