sys_ux_client_script.required_translations:
[ {
  "code" : "",
  "message" : "Unable to perform the action, please contact your system administrator.",
  "comment" : ""
}, {
  "code" : "",
  "message" : "Similar incidents",
  "comment" : ""
} ]

sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
async function handler({
    api,
    event,
    helpers,
    imports
}) {
    let actionStatus = {
        "actionIdentifier": event.payload.actionIdentifier
    };
    if (event.payload.actionKey && event.payload.actionKey == "VIEW_SIMILAR_INCIDENTS") {
        try {
            let clientPayload = event.payload.clientPayload;
            clientPayload = JSON.parse(clientPayload);
            let query = "sys_idIN" + clientPayload.sysIds.join();
            api.emit("NAV_ITEM_SELECTED", {
                "route": clientPayload.route,
                "targetRoute": clientPayload.targetRoute,
                "fields": {
                    "table": clientPayload.table,
                },
                "params": {
                    "listTitle": await helpers.translate("Similar incidents"),
                    "listView": clientPayload.view,
                    "query": query
                }
            });
            actionStatus.status = "cancelled";
            api.setState("actionExecutionStatus", actionStatus);
            return;
        } catch (e) {}
    }
    actionStatus.status = "error";
    actionStatus.errorCode = 40002;
    actionStatus.message = await helpers.translate("Unable to perform the action, please contact your system administrator.");
    api.setState("actionExecutionStatus", actionStatus);
}
